#include "findallanagrams.h"
#include <iostream>
#include <unordered_set>
#include "testing/SimpleTest.h"

void producePermutation(string& left, string& soFar, unordered_set<string>& perm) {
    if (left.empty()) {
        perm.insert(soFar);
        cout << soFar << endl;
        return;
    }

    for (int i = 0; i < left.size(); i++) {
        // choose
        char chosen = left[i];
        left = left.substr(0, i) + left.substr(i+1);
        soFar.push_back(chosen);
        producePermutation(left, soFar, perm);
        // unchoose
        left = left.substr(0, i) + chosen + left.substr(i);
        soFar.pop_back();
    }
}

/**
 * @brief findAnagrams_sol1
 * @param s: original string
 * @param p: matching string
 * @return all matchin anagrams
 * Solution 1 uses the method called move and query. We will build a set to store
 * the permutation of string p, and make a window slide from left to right. At
 * each step query if substring within window is in set. If so, got it!
 *
 * T: O(Nlog(M!)) when time for constructing permutation is excluded, which is T(M!)
 */
vector<int> findAnagrams_sol1(string s, string p) {
    if (p.length() > s.length()) return {};
    // TODO: build a set of permutations of p
    vector<int> result;
    unordered_set<string> permutations;
    string soFar;
    producePermutation(p, soFar, permutations);

    // TODO: move and query
    int begin = 0;
    int end = 0 + p.length();
    while (end <= s.length()) {
        string substr(s.begin() + begin, s.begin() + end);
        if (permutations.find(substr) != permutations.end()) {
            result.push_back(begin);
        }
        begin++;
        end++;
    }

    return result;
}

/**
 * How to avoid constructing permutations. Notice that the nature of permutation
 * here is frequency. So solution 2 will design two vecotrs to record frequency
 * of s and p
 *
 * ex>  cbaebabacd     p = abc
 *   fp = '1110000...000'
 *         ^^^
 *         abcdefg...xyz
 *   fs = '1110000...000'
 *         ^^^
 *         abcdefg...xyz
 * window 'cbaebabacd'
 *         ^ ^
 *
 * So move and check is what solution 2 does.
 *
 * T: O(N)
 */
vector<int> findAnagrams_sol2(string s, string p) {
    if (p.length() > s.length()) return {};
    vector<int> result;
    vector<int> ss(26, 0);
    vector<int> sp(26, 0);
    for (int i = 0; i < p.length(); i++) {
        sp[p[i] - 'a']++;
        ss[s[i] - 'a']++;
    }

    int begin = 0;
    int end = begin + p.length();
    while (end <= s.length()) {
        if (sp == ss) {
            result.push_back(begin);
        }
        ss[s[begin] - 'a']--;
        if (end < s.length()) {
            ss[s[end] - 'a']++;
        }
        begin++;
        end++;
    }

    return result;
}

